import discord
import random

from discord.ext import commands


class Fun(commands.Cog):

    def __init__(self, bot):
        self.bot = bot

    @commands.command()
    async def ban(self, ctx, member: discord.Member):

        await ctx.channel.purge(limit=1)
        await ctx.channel.send(f'Banning {member.mention}...')

    @commands.command(aliases=["delserver", "del-server"])
    @commands.bot_has_any_role("Big pp", "Cardinal", "Pope (entshuldigung)", "Pyrocynical's Brother")
    async def del_server(self, ctx):

        await ctx.channel.purge(limit=1)
        await ctx.send("Deleting server in 1 minute...")

    @del_server.error
    async def del_server_error(self, ctx, error):
        if isinstance(error, commands.MissingPermissions):
            await ctx.send("https://tenor.com/view/spy-laugh-spy-tf2-gif-7843426")

    @commands.command()
    async def eat_dog(self, ctx):

        await ctx.channel.purge(limit=1)
        await ctx.send("https://www.youtube.com/watch?v=CgHVNA8olaE")

    @commands.command(aliases=["gay-o-meter"])
    async def gay_o_meter(self, ctx, member: discord.Member):

        await ctx.channel.purge(limit=1)

        if member is None:

            await ctx.send("That member doesn't exist.")

        else:
            await ctx.send(f"{member.mention} is {random.randint(0, 100)}% gay.")

    @commands.command()
    async def hamburger(self, ctx):

        await ctx.channel.purge(limit=1)
        await ctx.send("https://youtu.be/El1BhIQFMfs")
        await ctx.send("hambuga chesbuga begmac whapa")

    @commands.command()
    async def pi(self, ctx):

        channel_id = self.bot.get_channel(709669976390500422)

        await ctx.channel.purge(limit=1)
        await channel_id.send(
            "3....")
        await channel_id.send(f"{ctx.author.mention}")

    @commands.command()
    @commands.has_any_role('Big pp')
    async def ping_multiple(self, ctx, member: discord.Member, loops):

        await ctx.channel.purge(limit=1)

        loopz = int(loops)
        num = 0
        while num < loopz + 1:
            await ctx.send({member.mention})
            loopz += 1


def setup(bot):
    bot.add_cog(Fun(bot))
